(13, 'Seksi Penyehatan Lingkungan', '', 2, '<table border="0" cellpadding="0" cellspacing="0" style="width:690px;" width="518">\r\n	<tbody>\r\n		<tr>\r\n			<td style="width:560px;height:47px;">\r\n				<p style="margin-left:18.0pt;">\r\n					(1)&nbsp; Seksi Penyehatan Lingkungan mempunyai tugas pokok melaksanakan urusan pemerintahan dibidang penyehatan lingkungan.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:25px;">\r\n				<p style="margin-left:18.0pt;">\r\n					(2)&nbsp; Untuk melaksanakan tugas pokok sebagaimana dimaksud pada ayat (1) pasal ini, Seksi Penyehatan Lingkungan mempunyai fungsi :</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:46px;">\r\n				<ol style="list-style-type:lower-alpha;">\r\n					<li>\r\n						Pelaksanaan penyehatan lingkungan meliputi penyehatan lingkungan pemukiman, penyehatan tempat-tempat umum, tempat pengelolaan makanan dan industri serta pengawasan kualitas air.</li>\r\n				</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:8px;">\r\n				<ol style="list-style-type:lower-alpha;">\r\n					<li value="2">\r\n						pelaksanaan rencana kegiatan mengacu pada rencana kerja Bidang Pengendalian, Pencegahan Penyakit dan Penyehatan Lingkungan;</li>\r\n				</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:17px;">\r\n				<ol style="list-style-type:lower-alpha;">\r\n					<li value="3">\r\n						pengumpulan, pengolahan data dan informasi, inventarisasi permasalahan serta melaksanakan pemecahan permasalahan yang berkaitan dengan&nbsp;&nbsp; penyehatan lingkungan;</li>\r\n				</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:31px;">\r\n				<ol style="list-style-type:lower-alpha;">\r\n					<li value="4">\r\n						perencanaan, pelaksanaan, pengendalian, evaluasi dan pelaporan kegiatan Seksi;</li>\r\n				</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:20px;">\r\n				<ol style="list-style-type:lower-alpha;">\r\n					<li value="5">\r\n						perumusan bahan kebijakan, bimbingan dan pembinaan serta petunjuk teknis yang berkaitan dengan penyehatan lingkungan;</li>\r\n				</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:20px;">\r\n				<ol style="list-style-type:lower-alpha;">\r\n					<li value="6">\r\n						pelaksanaan analisis dan pengembangan kinerja seksi;dan</li>\r\n				</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:20px;">\r\n				<ol style="list-style-type:lower-alpha;">\r\n					<li value="7">\r\n						<table cellpadding="0" cellspacing="0">\r\n							<tbody>\r\n								<tr>\r\n									<td align="left" height="29">\r\n										<table cellpadding="0" cellspacing="0" width="100%">\r\n											<tbody>\r\n												<tr>\r\n													<td>\r\n														<div>\r\n															<p align="right">\r\n																Bagian&hellip;..</p>\r\n														</div>\r\n													</td>\r\n												</tr>\r\n											</tbody>\r\n										</table>\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n						pelaksanaan tugas lain sesuai bidang tugasnya yang diberikan oleh Kepala Bidang.</li>\r\n				</ol>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p>\r\n	Seksi Penyehatan Lingkungan</p>\r\n', 11),
(14, 'Bidang Perbekalan Kesehatan, Pengawasan Obat dan Makanan<', '', 1, '<table border="0" cellpadding="0" cellspacing="0" style="width:690px;" width="518">\r\n	<tbody>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:18.0pt;">\r\n					1)&nbsp;&nbsp;&nbsp; Bidang Perbekalan Kesehatan, Pengawasan Obat dan Makanan mempunyai tugas pokok melaksanakan urusan pemerintahan dibidang penyelenggaraan obat, makanan dan perbekalan kesehatan.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:18.0pt;">\r\n					2)&nbsp;&nbsp;&nbsp; Untuk melaksanakan tugas pokok sebagaimana dimaksud pada ayat (1) pasal ini, Bidang Perbekalan Kesehatan, Pengawasan Obat dan makanan mempunyai fungsi melaksanakan urusan pemerintahan dibidang kesehatan, meliputi :</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					a.&nbsp;&nbsp;&nbsp; Penyusunan rencana kerja bidang mengacu pada rencana strategis dinas</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					b.&nbsp;&nbsp;&nbsp; Penyusunan dan perumusan bahan kebijakan dan petunjuk teknis bidang perbekalan kesehatan, obat dan makanan;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					c.&nbsp;&nbsp;&nbsp;&nbsp; Penyelenggaraan ketersediaan, pemerataan, mutu obat dan keterjangkauan harga obat serta perbekalan kesehatan dan vaksin;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					d.&nbsp;&nbsp;&nbsp; Peningkatan pengawasan dan akuntabilitas perbekalan kesehatan, obat dan makanan;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					e.&nbsp;&nbsp;&nbsp; penyusunan rencana kerja bidang mengacu pada rencana strategis dinas;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					f.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan pengumpulan data, informasi, inventarisasi dan pemecahan permasalahan yang berkaitan dengan&nbsp; regulasi dan perbekalan kesehatan, pengawasan obat dan makanan;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					g.&nbsp;&nbsp;&nbsp; perencanaan, pengkoordinasian, pelaksanaan, pengendalian, evaluasi dan pelaporan&nbsp; pengawasan dan pengendalian kegiatan Bidang;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					h.&nbsp;&nbsp;&nbsp; pelaksanaan analisis dan pengembangan kinerja Bidang;dan</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					i.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan tugas lain sesuai dengan bidang tugasnya yang diberikan oleh Kepala Dinas.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p>\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p>\r\n					&nbsp;</p>\r\n				<table cellpadding="0" cellspacing="0">\r\n					<tbody>\r\n						<tr>\r\n							<td align="left" height="29">\r\n								<table cellpadding="0" cellspacing="0" width="100%">\r\n									<tbody>\r\n										<tr>\r\n											<td>\r\n												<div>\r\n													<p align="right">\r\n														Pasal&hellip;..</p>\r\n												</div>\r\n											</td>\r\n										</tr>\r\n									</tbody>\r\n								</table>\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n				<p>\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p>\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p align="center">\r\n					Pasal 23</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p>\r\n					Bidang Perbekalan Kesehatan, Pengawasan Obat dan makanan, terdiri dari:</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:18.0pt;">\r\n					a.&nbsp;&nbsp;&nbsp; Seksi Perbekalan Kesehatan;dan</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:18.0pt;">\r\n					b.&nbsp;&nbsp;&nbsp; Seksi Pengawasan Obat dan Makanan</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p>\r\n	Bidang Perbekalan Kesehatan, Pengawasan Obat dan Makanan&lt;</p>\r\n', 0),
(15, 'Seksi Perbekalan Kesehatan', '', 2, '<table border="0" cellpadding="0" cellspacing="0" style="width:690px;" width="518">\r\n	<tbody>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:18.0pt;">\r\n					(1)&nbsp; Seksi Perbekalan Kesehatan mempunyai tugas pokok melaksanakan urusan pemerintahan dibidang perbekalan kesehatan.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:18.0pt;">\r\n					(2)&nbsp; Untuk melaksanakan tugas pokok sebagaimana dimaksud pada ayat (1) pasal ini, Seksi Perbekalan Kesehatan mempunyai fungsi :</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					a.&nbsp;&nbsp;&nbsp; Pelaksanaan penyediaan dan pengelolaan obat pelayanan kesehatan dasar, alat kesehatan habis pakai, reagensia dan vaksin skala kota;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:35.45pt;">\r\n					b.&nbsp;&nbsp;&nbsp; Pelaksanaan rencana kerja mengacu pada rencana kerja Bidang Perbekalan Kesehatan, Pengawasan Obat dan Makanan;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:35.45pt;">\r\n					c.&nbsp;&nbsp;&nbsp;&nbsp; pengumpulan,(pengolahan) mengolah data dan informasi, menginventarisasi permasalahan serta melaksanakan pemecahan permasalahan yang berkaitan dengan pengelolaan perbekalan kesehatan;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:35.45pt;">\r\n					d.&nbsp;&nbsp;&nbsp; perencanaan, pelaksanaan, pengendalian, evaluasi dan pelaporan kegiatan Seksi;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:35.45pt;">\r\n					e.&nbsp;&nbsp;&nbsp; perumusan, bimbingan, pembinaan dan pelaksanaan bahan kebijakan dan petunjuk teknis yang berkaitan dengan perbekalan kesehatan;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:35.45pt;">\r\n					f.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan analisis dan pengembangan kinerja Seksi;dan</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:35.45pt;">\r\n					g.&nbsp;&nbsp;&nbsp; pelaksanaan tugas lain sesuai bidang tugasnya yang diberikan oleh Kepala Bidang.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p>\r\n	Seksi Perbekalan Kesehatan</p>\r\n', 14),
(16, 'Seksi Pengawasan Obat & Makanan', '', 2, '<table border="0" cellpadding="0" cellspacing="0" style="width:690px;" width="518">\r\n	<tbody>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:18.0pt;">\r\n					(1)&nbsp; Seksi&nbsp; Pengawasan Obat dan Makanan mempunyai tugas pokok melaksanakan urusan pemerintahan dibidang pengawasan obat dan makanan.&nbsp;&nbsp;&nbsp; dihapus perbekalan kesehatan.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:18.0pt;">\r\n					(2)&nbsp; Untuk melaksanakan tugas pokok sebagaimana dimaksud pada ayat (1) pasal ini, Seksi Pengawasan Obat dan Makanan mempunyai fungsi:</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					a.&nbsp;&nbsp;&nbsp; Melaksanakan pembinaan dan pengawasan serta pemberian rekomendasi izin terhadap sarana farmasi (Apotik,Toko obat,Depo obat,Instalasi Farmasi,PBF,PAK,IKOT,IOT,Toko Kosmetik,Toko Alkes,PKRT)</p>\r\n				<p style="margin-left:36.0pt;">\r\n					b.&nbsp;&nbsp;&nbsp; Melaksanakan pembinaan dan pengawasan terhadap sarana produksi dan distribusi pangan .</p>\r\n				<p style="margin-left:36.0pt;">\r\n					c.&nbsp;&nbsp;&nbsp;&nbsp; Melaksanakan pembinaan dan pengawasan terhadap sarana distribusi sediaan farmasi.</p>\r\n				<p style="margin-left:36.0pt;">\r\n					d.&nbsp;&nbsp;&nbsp; Melaksanakan pemeriksaan setempat sarana distribusi sediaan farmasi dan pangan.</p>\r\n				<p style="margin-left:36.0pt;">\r\n					e.&nbsp;&nbsp;&nbsp; Melaksanakan sertifikasi produk pangan industri rumah tangga</p>\r\n				<p style="margin-left:36.0pt;">\r\n					f.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Melaksanakan sampling terhadap sediaan farmasi dan pangan</p>\r\n				<p style="margin-left:36.0pt;">\r\n					g.&nbsp;&nbsp;&nbsp; Melaksanakan pencegahan dan pengendalian keracunan akibat pangan .</p>\r\n				<p style="margin-left:36.0pt;">\r\n					h.&nbsp;&nbsp;&nbsp; Melaksanakan rencana kerja mengacu pada rencana kerja bidang Perbekalan kesehatan dan POM</p>\r\n				<p style="margin-left:36.0pt;">\r\n					i.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Melaksanakan pengumpulan ,pengolahan data dan informasi,inventarisasi permasalahan serta melaksanakan pemecahan masalah yang berkaitan dengan pengawasan obat dan makanan.</p>\r\n				<p style="margin-left:36.0pt;">\r\n					j.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Melaksanakan perencanaan ,pelaksanaan,evaluasi dan pelaporan kegiatan seksi</p>\r\n				<p style="margin-left:36.0pt;">\r\n					k.&nbsp;&nbsp;&nbsp;&nbsp; Melaksanakan perumusan bahan kebijakan ,bimbingan dan pembinaan serta petunjuk teknis yang berkaitan dengan pengawasan obat dan makanan</p>\r\n				<p style="margin-left:36.0pt;">\r\n					l.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Melaksanakan analisis dan pengembangan kinerja seksi</p>\r\n				<p style="margin-left:38.8pt;">\r\n					m.&nbsp; pengendalianPengendalian, pengawasan dan rekomendasi pemberian ijin Pedagang Besar Farmasi, alat kesehatan, Industri obat tradisional, Industri Kecil Obat Tradisional,Kosmetika, apotik, toko obat dan Sertifikasi Pangan Industri Rumah Tangga;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					n.&nbsp;&nbsp;&nbsp; Pembinaan, Pengawasan dan registrasi makanan minuman produksi rumah tangga dan&nbsp; sarana produksi Makanan Minuman, Sarana distribusi sediaan farmasi dan Makanan Minuman;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					o.&nbsp;&nbsp;&nbsp; Pencegahan dan pengendalian keracunan akibat pangan;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					p.&nbsp;&nbsp;&nbsp; pelaksanaan rencana kegiatan mengacu pada rencana kerja Bidang Perbekalan Kesehatan, Pengawasan Obat dan Makanan;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					q.&nbsp;&nbsp;&nbsp; pengumpulan, pengolahan data dan informasi, inventarisasi permasalahan serta melaksanakan pemecahan permasalahan yang berkaitan dengan&nbsp;&nbsp; pengawasan dan pengendalian peredaran obat dan makanan;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					r.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; perencanaan, pelaksanaan, pengendalian, evaluasi dan pelaporan kegiatan Seksi;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					s.&nbsp;&nbsp;&nbsp; perumusan, bimbingan, pembinaan dan pelaksanaan bahan kebijakan dan petunjuk teknis yang berkaitan dengan pengawasan dan pengendalian peredaran obat dan makanan;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					t.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan analisis dan pengembangan kinerja Seksi;dan</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:560px;height:29px;">\r\n				<p style="margin-left:36.0pt;">\r\n					u.&nbsp;&nbsp;&nbsp;</p>\r\n				<table cellpadding="0" cellspacing="0">\r\n					<tbody>\r\n						<tr>\r\n							<td align="left" height="28">\r\n								<table cellpadding="0" cellspacing="0" width="100%">\r\n									<tbody>\r\n										<tr>\r\n											<td>\r\n												<div>\r\n													<p align="right">\r\n														Bagian&hellip;..</p>\r\n												</div>\r\n											</td>\r\n										</tr>\r\n									</tbody>\r\n								</table>\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n				pelaksanaan tugas lain sesuai bidang tugasnya yang diberikan oleh Kepala Bidang.\r\n				<p>\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p>\r\n	Seksi Pengawasan Obat &amp; Makanan</p>\r\n', 14),
